#!/usr/bin/env python3\n\"\"\"\nConvenience wrapper for the TKA test runner.\n\nThis script simply calls the main test runner from its new organized location.\n\"\"\"\n\nimport subprocess\nimport sys\nfrom pathlib import Path\n\ndef main():\n    \"\"\"Run the main test suite from the organized location.\"\"\"\n    test_runner_path = Path(__file__).parent / \"scripts\" / \"testing\" / \"run_all_tests.py\"\n    \n    if not test_runner_path.exists():\n        print(f\"âŒ Test runner not found at: {test_runner_path}\")\n        print(\"Please check that the testing scripts are properly organized.\")\n        sys.exit(1)\n    \n    print(f\"ğŸš€ Running TKA test suite from: {test_runner_path}\")\n    print(\"=\" * 60)\n    \n    # Run the test suite\n    result = subprocess.run([sys.executable, str(test_runner_path)], cwd=Path.cwd())\n    sys.exit(result.returncode)\n\nif __name__ == \"__main__\":\n    main()\n