<!--\n\tGlass Scrollbar Demo Component\n\n\tShows all the different ways to apply beautiful glass scrollbars.\n\tUse this as a reference for implementing in your own components.\n-->\n<script lang=\"ts\">\n\timport { onMount } from 'svelte';\n\timport { applyQuickGlassScrollbar } from '$lib/utils/quickGlassScrollbars';\n\timport SimpleGlassScroll from '$lib/components/ui/SimpleGlassScroll.svelte';\n\n\t// Demo content\n\tconst demoContent = Array.from({ length: 50 }, (_, i) => `Demo item ${i + 1}`);\n\n\t// Reference for programmatic application\n\tlet programmableElement: HTMLElement;\n\n\tonMount(() => {\n\t\t// Example of programmatic application\n\t\tapplyQuickGlassScrollbar(programmableElement, 'gradient');\n\t});\n</script>\n\n<div class=\"demo-container\">\n\t<h1>ðŸŽ¨ Glass Scrollbar Showcase</h1>\n\t<p>Beautiful glassmorphism scrollbars for your TKA application</p>\n\n\t<div class=\"demo-grid\">\n\t\t<!-- Method 1: SimpleGlassScroll Component -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>1. SimpleGlassScroll Component</h3>\n\t\t\t<p class=\"method-desc\">Recommended for new components</p>\n\t\t\t<SimpleGlassScroll variant=\"primary\" height=\"200px\">\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</SimpleGlassScroll>\n\t\t\t<code>&lt;SimpleGlassScroll variant=\"primary\"&gt;</code>\n\t\t</div>\n\n\t\t<!-- Method 2: CSS Classes -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>2. CSS Class Application</h3>\n\t\t\t<p class=\"method-desc\">Quick and easy for existing elements</p>\n\t\t\t<div class=\"demo-scroll-area quick-glass-secondary\">\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t\t<code>class=\"quick-glass-secondary\"</code>\n\t\t</div>\n\n\t\t<!-- Method 3: Data Attributes -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>3. Data Attribute (Auto-applied)</h3>\n\t\t\t<p class=\"method-desc\">Automatic application on page load</p>\n\t\t\t<div class=\"demo-scroll-area\" data-glass=\"minimal\">\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t\t<code>data-glass=\"minimal\"</code>\n\t\t</div>\n\n\t\t<!-- Method 4: Programmatic -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>4. Programmatic Application</h3>\n\t\t\t<p class=\"method-desc\">Applied via JavaScript</p>\n\t\t\t<div class=\"demo-scroll-area\" bind:this={programmableElement}>\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t\t<code>applyQuickGlassScrollbar(element, 'gradient')</code>\n\t\t</div>\n\n\t\t<!-- Hover Variant -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>5. Hover-Only Scrollbar</h3>\n\t\t\t<p class=\"method-desc\">Appears only when you hover</p>\n\t\t\t<div class=\"demo-scroll-area quick-glass-hover\">\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t\t<code>class=\"quick-glass-hover\"</code>\n\t\t</div>\n\n\t\t<!-- Gradient Variant -->\n\t\t<div class=\"demo-section\">\n\t\t\t<h3>6. Gradient Scrollbar</h3>\n\t\t\t<p class=\"method-desc\">Enhanced visual effects</p>\n\t\t\t<SimpleGlassScroll variant=\"gradient\" height=\"200px\">\n\t\t\t\t<div class=\"demo-content\">\n\t\t\t\t\t{#each demoContent as item}\n\t\t\t\t\t\t<div class=\"demo-item special\">{item}</div>\n\t\t\t\t\t{/each}\n\t\t\t\t</div>\n\t\t\t</SimpleGlassScroll>\n\t\t\t<code>&lt;SimpleGlassScroll variant=\"gradient\"&gt;</code>\n\t\t</div>\n\t</div>\n\n\t<!-- Usage Instructions -->\n\t<div class=\"instructions\">\n\t\t<h2>ðŸš€ How to Use</h2>\n\t\t<div class=\"instruction-grid\">\n\t\t\t<div class=\"instruction-card\">\n\t\t\t\t<h4>Quick Start</h4>\n\t\t\t\t<p>Add to any scrollable element:</p>\n\t\t\t\t<code>class=\"quick-glass-primary\"</code>\n\t\t\t</div>\n\t\t\t<div class=\"instruction-card\">\n\t\t\t\t<h4>Auto-Apply</h4>\n\t\t\t\t<p>Use data attributes:</p>\n\t\t\t\t<code>data-glass=\"secondary\"</code>\n\t\t\t</div>\n\t\t\t<div class=\"instruction-card\">\n\t\t\t\t<h4>Component</h4>\n\t\t\t\t<p>Use the Svelte component:</p>\n\t\t\t\t<code>&lt;SimpleGlassScroll&gt;</code>\n\t\t\t</div>\n\t\t</div>\n\t</div>\n</div>\n\n<style>\n\t.demo-container {\n\t\tpadding: 2rem;\n\t\tmax-width: 1200px;\n\t\tmargin: 0 auto;\n\t\tbackground: var(--desktop-bg-secondary);\n\t\tborder-radius: var(--desktop-border-radius);\n\t\tcolor: var(--desktop-text-primary);\n\t}\n\n\th1 {\n\t\ttext-align: center;\n\t\tmargin-bottom: 1rem;\n\t\tfont-family: var(--desktop-font-family);\n\t\tcolor: var(--desktop-text-primary);\n\t}\n\n\tp {\n\t\ttext-align: center;\n\t\tmargin-bottom: 2rem;\n\t\tcolor: var(--desktop-text-secondary);\n\t}\n\n\t.demo-grid {\n\t\tdisplay: grid;\n\t\tgrid-template-columns: repeat(auto-fit, minmax(300px, 1fr));\n\t\tgap: 2rem;\n\t\tmargin-bottom: 3rem;\n\t}\n\n\t.demo-section {\n\t\tbackground: var(--desktop-bg-tertiary);\n\t\tborder: 1px solid var(--desktop-border-secondary);\n\t\tborder-radius: var(--desktop-border-radius);\n\t\tpadding: 1.5rem;\n\t}\n\n\t.demo-section h3 {\n\t\tmargin: 0 0 0.5rem 0;\n\t\tcolor: var(--desktop-text-primary);\n\t\tfont-size: 1.1rem;\n\t}\n\n\t.method-desc {\n\t\tmargin: 0 0 1rem 0;\n\t\tfont-size: 0.9rem;\n\t\tcolor: var(--desktop-text-muted);\n\t\ttext-align: left;\n\t}\n\n\t.demo-scroll-area {\n\t\theight: 200px;\n\t\tborder: 1px solid var(--desktop-border-tertiary);\n\t\tborder-radius: 6px;\n\t\tmargin-bottom: 1rem;\n\t\tbackground: var(--desktop-bg-secondary);\n\t}\n\n\t.demo-content {\n\t\tpadding: 1rem;\n\t}\n\n\t.demo-item {\n\t\tpadding: 0.75rem 1rem;\n\t\tmargin-bottom: 0.5rem;\n\t\tbackground: var(--desktop-bg-primary);\n\t\tborder: 1px solid var(--desktop-border-tertiary);\n\t\tborder-radius: 4px;\n\t\tcolor: var(--desktop-text-primary);\n\t\ttransition: all 0.2s ease;\n\t}\n\n\t.demo-item:hover {\n\t\tbackground: var(--desktop-bg-secondary);\n\t\tborder-color: var(--desktop-border-primary);\n\t\ttransform: translateX(4px);\n\t}\n\n\t.demo-item.special {\n\t\tbackground: linear-gradient(90deg, var(--desktop-primary-blue), var(--desktop-restart-blue));\n\t\tcolor: white;\n\t\tborder: none;\n\t}\n\n\tcode {\n\t\tdisplay: block;\n\t\tpadding: 0.5rem;\n\t\tbackground: rgba(0, 0, 0, 0.3);\n\t\tborder-radius: 4px;\n\t\tfont-family: 'Courier New', monospace;\n\t\tfont-size: 0.85rem;\n\t\tcolor: #4fd1c7;\n\t\tborder: 1px solid rgba(255, 255, 255, 0.1);\n\t}\n\n\t.instructions {\n\t\tbackground: var(--desktop-bg-primary);\n\t\tborder: 1px solid var(--desktop-border-primary);\n\t\tborder-radius: var(--desktop-border-radius);\n\t\tpadding: 2rem;\n\t}\n\n\t.instructions h2 {\n\t\tmargin: 0 0 1.5rem 0;\n\t\tcolor: var(--desktop-text-primary);\n\t\ttext-align: center;\n\t}\n\n\t.instruction-grid {\n\t\tdisplay: grid;\n\t\tgrid-template-columns: repeat(auto-fit, minmax(250px, 1fr));\n\t\tgap: 1rem;\n\t}\n\n\t.instruction-card {\n\t\tbackground: var(--desktop-bg-secondary);\n\t\tborder: 1px solid var(--desktop-border-secondary);\n\t\tborder-radius: 8px;\n\t\tpadding: 1.5rem;\n\t\ttext-align: center;\n\t}\n\n\t.instruction-card h4 {\n\t\tmargin: 0 0 0.5rem 0;\n\t\tcolor: var(--desktop-text-primary);\n\t}\n\n\t.instruction-card p {\n\t\tmargin: 0 0 1rem 0;\n\t\tfont-size: 0.9rem;\n\t\tcolor: var(--desktop-text-secondary);\n\t}\n\n\t.instruction-card code {\n\t\tmargin: 0;\n\t\tfont-size: 0.8rem;\n\t}\n\n\t@media (max-width: 768px) {\n\t\t.demo-container {\n\t\t\tpadding: 1rem;\n\t\t}\n\n\t\t.demo-grid {\n\t\t\tgrid-template-columns: 1fr;\n\t\t}\n\n\t\t.instruction-grid {\n\t\t\tgrid-template-columns: 1fr;\n\t\t}\n\t}\n</style>\n